import React, { useState } from 'react';
import './index.css';
import { FaPlus, FaRegTrashAlt } from "react-icons/fa";


function App() {

  const [todos, setTodos] = useState();
  const [items, setItems] = useState([]);
  const inputItem = (e) => {
    setTodos(e.target.value);
  };
  const addItem = () => {
    setItems((oldItems) => {
      return [...oldItems, todos];
    });
    setTodos("");
  };

  return (
    <div className="App">
      <div className='todoLists'>
        <h1>My To-Do App</h1>
        <div className='todoInput'>
          <input type='text' placeholder='Add a new task' onChange={inputItem} />
          <button onClick={addItem}><FaPlus /></button>
        </div>
        <div className='taskList'>
          <div className='task'>
            <div className='task'>
              <ol>
                {/* {todos} */}
                {items.map((item) => (
                  <li>
                    <input type='checkbox' />
                    {item}
                  </li>
                )
                )
                }
                <button><FaRegTrashAlt /></button>

              </ol>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
