import React, { useState } from 'react';
import './index.css';
import { FaPlus, FaRegTrashAlt } from "react-icons/fa";


function App() {

  const [todos, setTodos] = useState();
  const [items, setItems] = useState([]);
  const inputItem = (e) => {
    setTodos(e.target.value);
  };
  const addItem = () => {
    setItems((oldItems) => {
      return [...oldItems, todos];
    });
    setTodos("");
  };
  const delItem = (id) => {
    setItems((oldItems) => { 
      return oldItems.filter((arrElem, index) => {
        return index !== id;
      });
    });
  };

  return (
    <div className="App">
      <div className='todoLists'>
        <h1>My To-Do App</h1>
        <div className='todoInput'>
          <input type='text'
            placeholder='Add a new task'
            value={todos}
            required
            onChange={inputItem}
          />
          <button onClick={addItem}><FaPlus /></button>
        </div>
        <div className='taskList'>
          <div className='task'>
            <ol>
              {items.map((item) => (
                <>
                  <div className='task'>
                    <button onClick={delItem}>
                      <FaRegTrashAlt />
                    </button>
                    <input type='checkbox' />
                    <li>
                      {item}
                    </li>
                  </div>
                </>
              ))}
            </ol>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
