import React from 'react'

function ToDo(props) {

    const addItem = () => {
        setItems((oldItems) => {
            return [...oldItems, todos];
        });
        setTodos("");
    };

    const delItem = (id) => {
        setItems((oldItems) => {
            return oldItems.filter((arrElem, index) => {
                return index !== id;
            });
        });
    };

    return (
        <div className='task'>
            <button onClick={delItem(index)}>
                <FaRegTrashAlt />
            </button>
            <input type='checkbox' />
            <li>
                {props.item}
            </li>
        </div>
    )
}

export default ToDo
