import React from 'react';
import './index.css';
import { FaPlus, AiOutlineDelete } from "react-icons/fa";


function App() {
  return (
    <div className="App">
      <div className='todoLists'>
        <h1>My To-Do App</h1>
        <div className='todoInput'>
          <input type='text' placeholder='Add a new task' />
          <button><FaPlus /></button>
        </div>
        <div className='taskList'>
          <div className='task'>
            <input type='checkbox' />
            <p>Task 1</p>
            <button><AiOutlineDelete /></button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
